import React, { useState } from 'react';
import Comptes from './Comptes';
import Transactions from './Transactions';

function App() {
  const [showComptes, setShowComptes] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  // Display accounts
  const handleComptesClick = () => {
    setShowComptes(true);
    setShowTransactions(false);
  };

  // Display Transactions
  const handleTransactionsClick = () => {
    setShowTransactions(true);
    setShowComptes(false);
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center"
      style={{
        backgroundImage: 'url(https://img.freepik.com/photos-gratuite/elements-vente-evenement-cyber-lundi-plat-laics-fond-noir_23-2148675055.jpg?semt=ais_hybrid)',
      }}
    >
      {/* Dark overlay for readability */}
      <div className="min-h-screen bg-black bg-opacity-70 flex flex-col items-center justify-center">
        {/* Header Section */}
        <div className="text-center text-white p-6">
          <h1 className="text-5xl font-extrabold mb-4 animate-bounce">
            Gestion des Comptes et Transactions
          </h1>
          <p className="text-lg mb-8 text-gray-300">
            Une solution intuitive pour gérer vos comptes et suivre vos transactions en toute simplicité.
          </p>

          {/* Buttons */}
          <div className="flex justify-center space-x-6">
            <button
              onClick={handleComptesClick}
              className="bg-blue-500 text-white px-8 py-3 rounded-lg font-bold hover:bg-blue-600 transition transform hover:scale-105 shadow-lg"
            >
              Voir les Comptes
            </button>
            <button
              onClick={handleTransactionsClick}
              className="bg-green-500 text-white px-8 py-3 rounded-lg font-bold hover:bg-green-600 transition transform hover:scale-105 shadow-lg"
            >
              Voir les Transactions
            </button>
          </div>
        </div>

        {/* Content Section */}
        <div className="w-full max-w-5xl mt-10 p-6">
          {showComptes && (
            <div className="bg-white p-6 rounded-lg shadow-2xl animate-fade-in">
              <Comptes />
            </div>
          )}
          {showTransactions && (
            <div className="bg-white p-6 rounded-lg shadow-2xl animate-fade-in">
              <Transactions />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
