import React, { useState } from 'react';
import Comptes from './Comptes';
import Transactions from './Transactions';

function App() {
  const [showComptes, setShowComptes] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  // Display accounts
  const handleComptesClick = () => {
    setShowComptes(true);
    setShowTransactions(false);
  };

  // Display transactions
  const handleTransactionsClick = () => {
    setShowTransactions(true);
    setShowComptes(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Header */}
      <header className="sticky top-0 z-50 bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 shadow-lg">
        <div className="container mx-auto px-6 py-4 flex items-center justify-between">
          <h1 className="text-3xl font-extrabold tracking-wider">Finance Manager</h1>
          <nav>
            <ul className="flex space-x-8">
              <li>
                <a
                  href="#"
                  className="text-white hover:text-gray-200 font-medium transition"
                >
                  Home
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-white hover:text-gray-200 font-medium transition"
                >
                  Features
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-white hover:text-gray-200 font-medium transition"
                >
                  Contact
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Main Section */}
      <main className="container mx-auto py-20 px-6">
        <div className="text-center">
          <h1 className="text-5xl font-bold mb-6 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 via-teal-400 to-green-400">
            Let's manage your accounts and Transactions 🤌
          </h1>

          {/* Buttons */}
          <div className="flex justify-center space-x-6">
            <button
              onClick={handleComptesClick}
              className="px-8 py-3 text-lg font-bold rounded-lg bg-gradient-to-r from-blue-500 to-teal-500 hover:from-blue-600 hover:to-teal-600 shadow-xl transform hover:scale-105 transition"
            >
              View Accounts
            </button>
            <button
              onClick={handleTransactionsClick}
              className="px-8 py-3 text-lg font-bold rounded-lg bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 shadow-xl transform hover:scale-105 transition"
            >
              View Transactions
            </button>
          </div>
        </div>

        {/* Content Section */}
        <div className="mt-16 grid grid-cols-1 md:grid-cols-2 gap-10">
          {showComptes && (
            <div className="bg-gray-800 rounded-lg p-6 shadow-lg transform transition hover:scale-105">
              <Comptes />
            </div>
          )}
          {showTransactions && (
            <div className="bg-gray-800 rounded-lg p-6 shadow-lg transform transition hover:scale-105">
              <Transactions />
            </div>
          )}
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 py-6">
        <div className="container mx-auto text-center text-gray-500">
          &copy; 2024 Finance Manager. All rights reserved.
        </div>
      </footer>
    </div>
  );
}

export default App;
