import React, { useState } from 'react';
import Comptes from './Comptes';
import Transactions from './Transactions';

function App() {
  const [showComptes, setShowComptes] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  // Display accounts
  const handleComptesClick = () => {
    setShowComptes(true);
    setShowTransactions(false);
  };

  // Display Transactions
  const handleTransactionsClick = () => {
    setShowTransactions(true);
    setShowComptes(false);
  };

  return (
    <div
      className="min-h-screen bg-cover bg-center"
      style={{
        backgroundImage:
          'url(https://images.unsplash.com/photo-1516375199441-5a64e6659b78?crop=entropy&cs=tinysrgb&fit=max&fm=jpg&q=80&w=1080)',
      }}
    >
      {/* Header Section */}
      <header className="bg-black bg-opacity-80 text-white py-4 px-8 shadow-md">
        <div className="container mx-auto flex items-center justify-between">
          <h1 className="text-2xl font-bold">My Finance App</h1>
          <nav>
            <ul className="flex space-x-6">
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition"
                >
                  Home
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition"
                >
                  About
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition"
                >
                  Contact
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <div className="min-h-screen bg-black bg-opacity-70 flex flex-col items-center justify-center">
        {/* Main Section */}
        <div className="text-center text-white p-6">
          <h1 className="text-5xl font-extrabold mb-4 animate-bounce">
            Let's ManageAccount and Transaction Management
          </h1>
          <p className="text-lg mb-8 text-gray-300">
            Manage your accounts and track transactions seamlessly with our modern interface.
          </p>

          {/* Buttons */}
          <div className="flex justify-center space-x-6">
            <button
              onClick={handleComptesClick}
              className="bg-gradient-to-r from-purple-500 via-pink-500 to-red-500 text-white px-8 py-3 rounded-lg font-bold hover:from-purple-600 hover:to-red-600 transition transform hover:scale-105 shadow-lg"
            >
              View Accounts
            </button>
            <button
              onClick={handleTransactionsClick}
              className="bg-gradient-to-r from-blue-500 to-teal-500 text-white px-8 py-3 rounded-lg font-bold hover:from-blue-600 hover:to-teal-600 transition transform hover:scale-105 shadow-lg"
            >
              View Transactions
            </button>
          </div>
        </div>

        {/* Content Section */}
        <div className="w-full max-w-5xl mt-10 p-6">
          {showComptes && (
            <div className="bg-white p-6 rounded-lg shadow-2xl animate-fade-in">
              <Comptes />
            </div>
          )}
          {showTransactions && (
            <div className="bg-white p-6 rounded-lg shadow-2xl animate-fade-in">
              <Transactions />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
