import React, { useState } from 'react';
import Comptes from './Comptes';
import Transactions from './Transactions';

function App() {
  const [showComptes, setShowComptes] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  // Display accounts
  const handleComptesClick = () => {
    setShowComptes(true);
    setShowTransactions(false);
  };

  // Display Transactions
  const handleTransactionsClick = () => {
    setShowTransactions(true);
    setShowComptes(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-900 via-gray-800 to-gray-900 text-white">
      {/* Navigation Bar */}
      <header className="sticky top-0 bg-gray-800 bg-opacity-90 shadow-lg z-50">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold tracking-wide text-indigo-400 hover:text-indigo-300 transition">
            My Finance App
          </h1>
          <nav>
            <ul className="flex space-x-8 text-sm">
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition duration-200"
                >
                  Home
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition duration-200"
                >
                  About
                </a>
              </li>
              <li>
                <a
                  href="#"
                  className="text-gray-300 hover:text-white transition duration-200"
                >
                  Contact
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="flex flex-col items-center justify-center text-center py-20 space-y-6">
        <h1 className="text-5xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-purple-500">
          Manage Your Finances Seamlessly
        </h1>
        <p className="text-gray-400 max-w-2xl">
          A modern way to track your accounts and transactions with ease. Stay
          informed and make smarter decisions!
        </p>
        <div className="space-x-6">
          <button
            onClick={handleComptesClick}
            className="px-6 py-3 rounded-lg text-lg font-medium bg-gradient-to-r from-blue-500 to-teal-500 hover:from-blue-600 hover:to-teal-600 transition transform hover:scale-105 shadow-lg"
          >
            View Accounts
          </button>
          <button
            onClick={handleTransactionsClick}
            className="px-6 py-3 rounded-lg text-lg font-medium bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 transition transform hover:scale-105 shadow-lg"
          >
            View Transactions
          </button>
        </div>
      </section>

      {/* Content Section */}
      <main className="container mx-auto p-6">
        {showComptes && (
          <div className="bg-gray-800 rounded-xl shadow-2xl p-8 animate-fade-in">
            <h2 className="text-2xl font-bold mb-4 text-indigo-400">
              Account Details
            </h2>
            <Comptes />
          </div>
        )}
        {showTransactions && (
          <div className="bg-gray-800 rounded-xl shadow-2xl p-8 animate-fade-in">
            <h2 className="text-2xl font-bold mb-4 text-purple-400">
              Transaction Details
            </h2>
            <Transactions />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
